            <span class="token keyword">if</span><span class="token punctuation">(</span>picking<span class="token punctuation">)</span><span class="token punctuation">{</span>
                <span class="token keyword">const</span> <span class="token punctuation">{</span>offsetX<span class="token punctuation">,</span> offsetY<span class="token punctuation">}</span> <span class="token operator">=</span> e
