                console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>  ctx<span class="token punctuation">.</span><span class="token function">getContextAttributes</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">)</span>
             
