    <span class="token operator">&lt;</span>title<span class="token operator">></span>Document<span class="token operator">&lt;</span><span class="token operator">/</span>title<span class="token operator">></span>
<span class="token operator">&lt;</span><span class="token operator">/</span>head<span class="token operator">></span>
<span class="token operator">&lt;</span>body<span class="token operator">></span>
    <span class="token operator">&lt;</span>button id<span class="token operator">=</span><span class="token string">"open_camera"</span><span class="token operator">></span>开启摄像头<span class="token operator">&lt;</span><span class="token operator">/</span>button<span class="token operator">></span>
    <span class="token operator">&lt;</span>button id<span class="token operator">=</span><span class="token string">"share_screen"</span><span class="token operator">></span>开启屏幕共享<span class="token operator">&lt;</span><span class="token operator">/</span>button<span class="token operator">></span>
    <span class="token operator">&lt;</span>button id<span class="token operator">=</span><span class="token string">"close_volume"</span><span class="token operator">></span>关闭麦克风<span class="token operator">&lt;</span><span class="token operator">/</span>button<span class="token operator">></span>
    <span class="token operator">&lt;</span>video id<span class="token operator">=</span><span class="token string">"open_video"</span> autoplay<span class="token operator">></span><span class="token operator">&lt;</span><span class="token operator">/</span>video<span class="token operator">></span>
    <span class="token operator">&lt;</span>video id<span class="token operator">=</span><span class="token string">"share_video"</span> autoplay<span class="token operator">></span><span class="token operator">&lt;</span><span class="token operator">/</span>video<span class="token operator">></span>
    <span class="token operator">&lt;</span>script<span class="token operator">></span>
        <span class="token keyword">const</span> open <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">"open_camera"</span><span class="token punctuation">)</span>
        <span class="token keyword">const</span> share <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">"share_screen"</span><span class="token punctuation">)</span>
        <span class="token keyword">const</span> open_video <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">"open_video"</span><span class="token punctuation">)</span>
        <span class="token keyword">const</span> share_video <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">"share_video"</span><span class="token punctuation">)</span>
        <span class="token keyword">const</span> close_volume <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">"close_volume"</span><span class="token punctuation">)</span>
        <span class="token keyword">let</span> cameraStream <span class="token operator">=</span> <span class="token keyword">null</span>
        <span class="token keyword">let</span> shareStream <span class="token operator">=</span> <span class="token keyword">null</span>
        <span class="token keyword">let</span> peerConnection <span class="token operator">=</span> <span class="token keyword">null</span> 
        <span class="token keyword">const</span> <span class="token function-variable function">openClickHandler</span> <span class="token operator">=</span> <span class="token keyword">async</span> <span class="token punctuation">(</span><span class="token parameter">e</span><span class="token punctuation">)</span><span class="token operator">=></span><span class="token punctuation">{</span>
            <span class="token keyword">if</span><span class="token punctuation">(</span><span class="token operator">!</span>cameraStream<span class="token punctuation">)</span><span class="token punctuation">{</span>
                <span class="token comment">// 获取摄像头的媒体流</span>
                <span class="token keyword">try</span><span class="token punctuation">{</span>
                    <span class="token keyword">const</span> stream <span class="token operator">=</span> <span class="token keyword">await</span> navigator<span class="token punctuation">.</span>mediaDevices<span class="token punctuation">.</span><span class="token function">getUserMedia</span><span class="token punctuation">(</span><span class="token punctuation">{</span><span class="token literal-property property">video</span><span class="token operator">:</span><span class="token boolean">true</span><span class="token punctuation">,</span><span class="token literal-property property">audio</span><span class="token operator">:</span><span class="token boolean">true</span><span class="token punctuation">}</span><span class="token punctuation">)</span>
                    cameraStream <span class="token operator">=</span> stream
                    open_video<span class="token punctuation">.</span>srcObject <span class="token operator">=</span> stream
                    open_camera<span class="token punctuation">.</span>textContent <span class="token operator">=</span> <span class="token string">'关闭摄像头'</span>
                <span class="token punctuation">}</span><span class="token keyword">catch</span><span class="token punctuation">(</span>err<span class="token punctuation">)</span><span class="token punctuation">{</span>
                    console<span class="token punctuation">.</span><span class="token function">warn</span><span class="token punctuation">(</span>err<span class="token punctuation">)</span>
                <span class="token punctuation">}</span>
                
            <span class="token punctuation">}</span><span class="token keyword">else</span><span class="token punctuation">{</span>
                cameraStream<span class="token punctuation">.</span><span class="token function">getTracks</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">forEach</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token parameter">track</span><span class="token punctuation">)</span><span class="token operator">=></span>track<span class="token punctuation">.</span><span class="token function">stop</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
                cameraStream <span class="token operator">=</span> <span class="token keyword">null</span>
                open_video<span class="token punctuation">.</span>srcObject <span class="token operator">=</span> <span class="token keyword">null</span>
                open_camera<span class="token punctuation">.</span>textContent <span class="token operator">=</span> <span class="token string">'开启摄像头'</span>
            <span class="token punctuation">}</span>
            console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>cameraStream<span class="token punctuation">)</span>
        <span class="token punctuation">}</span>
        <span class="token keyword">const</span> <span class="token function-variable function">shareClickHandler</span> <span class="token operator">=</span> <span class="token keyword">async</span> <span class="token punctuation">(</span><span class="token parameter">e</span><span class="token punctuation">)</span><span class="token operator">=></span><span class="token punctuation">{</span>
            <span class="token keyword">if</span><span class="token punctuation">(</span><span class="token operator">!</span>shareStream<span class="token punctuation">)</span><span class="token punctuation">{</span>
                <span class="token keyword">try</span> <span class="token punctuation">{</span>
                    peerConnection <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">RTCPeerConnection</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
                    <span class="token keyword">const</span> stream <span class="token operator">=</span> <span class="token keyword">await</span> navigator<span class="token punctuation">.</span>mediaDevices<span class="token punctuation">.</span><span class="token function">getDisplayMedia</span><span class="token punctuation">(</span><span class="token punctuation">{</span><span class="token literal-property property">video</span><span class="token operator">:</span><span class="token boolean">true</span><span class="token punctuation">}</span><span class="token punctuation">)</span>
                    shareStream <span class="token operator">=</span> stream
                    <span class="token comment">// 监听停止事件</span>
                    shareStream<span class="token punctuation">.</span><span class="token function">getVideoTracks</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span><span class="token punctuation">.</span><span class="token function-variable function">onended</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token operator">=></span><span class="token punctuation">{</span>
                        console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span><span class="token string">'关闭屏幕共享'</span><span class="token punctuation">)</span>
                    <span class="token punctuation">}</span>
                    <span class="token comment">// 获取轨道</span>
                    <span class="token keyword">const</span> screenTrack <span class="token operator">=</span> shareStream<span class="token punctuation">.</span><span class="token function">getVideoTracks</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span>
                    <span class="token comment">// 将轨道添加到RTCpeerConnect</span>
                    peerConnection<span class="token punctuation">.</span><span class="token function">addTrack</span><span class="token punctuation">(</span>screenTrack<span class="token punctuation">)</span>
                    console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>shareStream<span class="token punctuation">.</span><span class="token function">getVideoTracks</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">filter</span><span class="token punctuation">(</span><span class="token parameter">v</span><span class="token operator">=></span>v<span class="token punctuation">.</span>kind <span class="token operator">===</span> <span class="token string">'audio'</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
                    share_video<span class="token punctuation">.</span>srcObject <span class="token operator">=</span> shareStream
                    <span class="token comment">// 监听ICE候选事件</span>
                    peerConnection<span class="token punctuation">.</span><span class="token function-variable function">onicecandidate</span> <span class="token operator">=</span> <span class="token parameter">e</span><span class="token operator">=></span><span class="token punctuation">{</span>
                        <span class="token keyword">if</span><span class="token punctuation">(</span>e<span class="token punctuation">.</span>candidate<span class="token punctuation">)</span><span class="token punctuation">{</span>
                            <span class="token comment">// 这里一般通过websocker将e.candidate发送给服务端</span>
                            <span class="token comment">// console.log(e.candidate)</span>
                            <span class="token comment">// sendIceCandidate(e.candidate)</span>
                        <span class="token punctuation">}</span>
                    <span class="token punctuation">}</span>
                    share<span class="token punctuation">.</span>textContent <span class="token operator">=</span> <span class="token string">'关闭屏幕共享'</span>
                    <span class="token comment">// 创建offer并设置本地描述</span>
                    <span class="token keyword">const</span> offer <span class="token operator">=</span> <span class="token keyword">await</span> peerConnection<span class="token punctuation">.</span><span class="token function">createOffer</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
                    peerConnection<span class="token punctuation">.</span><span class="token function">setLocalDescription</span><span class="token punctuation">(</span>offer<span class="token punctuation">)</span>
                <span class="token punctuation">}</span> <span class="token keyword">catch</span> <span class="token punctuation">(</span>error<span class="token punctuation">)</span> <span class="token punctuation">{</span>
                    console<span class="token punctuation">.</span><span class="token function">warn</span><span class="token punctuation">(</span>error<span class="token punctuation">)</span>
                <span class="token punctuation">}</span>
            <span class="token punctuation">}</span><span class="token keyword">else</span><span class="token punctuation">{</span>
                shareStream <span class="token operator">=</span> <span class="token keyword">null</span>
                peerConnection <span class="token operator">=</span> <span class="token keyword">null</span>
                share_video<span class="token punctuation">.</span>srcObject <span class="token operator">=</span> <span class="token keyword">null</span>
                share<span class="token punctuation">.</span>textContent <span class="token operator">=</span> <span class="token string">'开启屏幕共享'</span>
            <span class="token punctuation">}</span>
        <span class="token punctuation">}</span>
        open<span class="token punctuation">.</span><span class="token function">addEventListener</span><span class="token punctuation">(</span><span class="token string">"click"</span><span class="token punctuation">,</span> openClickHandler<span class="token punctuation">)</span>
        share<span class="token punctuation">.</span><span class="token function">addEventListener</span><span class="token punctuation">(</span><span class="token string">"click"</span><span class="token punctuation">,</span> shareClickHandler<span class="token punctuation">)</span>
        
        close_volume<span class="token punctuation">.</span><span class="token function">addEventListener</span><span class="token punctuation">(</span><span class="token string">"click"</span><span class="token punctuation">,</span> <span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token operator">=></span><span class="token punctuation">{</span>
            <span class="token keyword">if</span><span class="token punctuation">(</span>cameraStream<span class="token punctuation">)</span><span class="token punctuation">{</span>
                <span class="token keyword">const</span> audioTrack <span class="token operator">=</span> cameraStream<span class="token punctuation">.</span><span class="token function">getAudioTracks</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">[</span><span class="token number">0</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
                audioTrack<span class="token punctuation">.</span>enabled <span class="token operator">=</span> <span class="token operator">!</span>audioTrack<span class="token punctuation">.</span>enabled<span class="token punctuation">;</span>
                close_volume<span class="token punctuation">.</span>textContent <span class="token operator">=</span> audioTrack<span class="token punctuation">.</span>enabled<span class="token operator">?</span> <span class="token string">'关闭麦克风'</span><span class="token operator">:</span><span class="token string">'开启麦克风'</span>
            <span class="token punctuation">}</span>
        <span class="token punctuation">}</span><span class="token punctuation">)</span>
    <span class="token operator">&lt;</span><span class="token operator">/</span>script<span class="token operator">></span>
<span class="token operator">&lt;</span><span class="token operator">/</span>body<span class="token operator">></span>
